import React, { useState, useRef, useEffect } from 'react';
import { 
  MessageCircle, 
  Send, 
  Loader, 
  Moon, 
  Sun, 
  Settings,
  Heart,
  Activity,
  Apple,
  Pill,
  Brain,
  Coffee,
  Cookie
} from 'lucide-react';

import './App.css';

// Predefined prompt suggestions
const PROMPT_SUGGESTIONS = [
  {
    id: 1,
    icon: <Apple className="w-4 h-4" />,
    title: "Diabetes Diet",
    prompt: "What are the best foods to eat to help manage my blood sugar levels?"
  },
  {
    id: 2,
    icon: <Activity className="w-4 h-4" />,
    title: "Heart Health",
    prompt: "Can you suggest some heart-friendly exercises I can do at home?"
  },
  {
    id: 3,
    icon: <Brain className="w-4 h-4" />,
    title: "Stress Management",
    prompt: "What are some effective ways to manage stress with my condition?"
  },
  {
    id: 4,
    icon: <Coffee className="w-4 h-4" />,
    title: "Daily Routine",
    prompt: "How should I structure my daily routine to better manage my health?"
  },
  {
    id: 5,
    icon: <Cookie className="w-4 h-4" />,
    title: "Nutritional content in food",
    prompt: "What are the nutritional contents in beef?"
  },
  {
    id: 6,
    icon: <Pill className="w-4 h-4" />,
    title: "Medications",
    prompt: "What should I know about timing my medications with meals?"
  }
];

export default function App() {
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);

  // State for modal visibility and chat history
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);

  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    document.body.classList.toggle('dark-mode', isDarkMode);

    // Fetch chat history on modal open
    if (isModalOpen) {
      fetchChatHistory();
    }

  }, [messages, isDarkMode, isModalOpen]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    const newMessage = { text: query, sender: 'user' };
    setMessages((prev) => [...prev, newMessage]);
    setQuery('');
    setIsLoading(true);

    try {
      const response = await fetch(`http://localhost:8000/search?query=${encodeURIComponent(query)}`);
      const data = await response.json();

      setMessages((prev) => [
        ...prev,
        { text: data.message, sender: 'bot' }
      ]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { text: 'Something went wrong. Please try again.', sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePromptClick = (prompt) => {
    setQuery(prompt);
  };

  // Fetch chat history from backend
  const fetchChatHistory = async () => {
    try {
      const response = await fetch('http://localhost:8000/chat-history');
      const data = await response.json();
      setChatHistory(data.history);
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  };

  // Toggle modal visibility
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  // Function to close modal when clicking outside the content
  const handleOverlayClick = (e) => {
    // Close modal if click is on the overlay (not on the content)
    if (e.target.classList.contains('modal-overlay')) {
      setIsModalOpen(false);
    }
  };

  return (
    <div className={`app-container ${isDarkMode ? 'dark-theme' : ''}`}>
      {/* Sidebar with Prompt Suggestions */}
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="new-chat-button">
            <Heart className="w-4 h-4 mr-2" />
            Medical Prompts
          </div>
        </div>

        <div className="prompts-list">
          {PROMPT_SUGGESTIONS.map((suggestion) => (
            <button
              key={suggestion.id}
              className="prompt-suggestion-item"
              onClick={() => handlePromptClick(suggestion.prompt)}
            >
              <div className="prompt-icon">
                {suggestion.icon}
              </div>
              <div className="prompt-content">
                <div className="prompt-title">{suggestion.title}</div>
                <div className="prompt-preview">
                  {suggestion.prompt.length > 40 
                    ? `${suggestion.prompt.substring(0, 40)}...` 
                    : suggestion.prompt}
                </div>
              </div>
            </button>
          ))}
        </div>

        <div className="sidebar-footer">
          <button 
            className="theme-toggle"
            onClick={() => setIsDarkMode(!isDarkMode)}
          >
            {isDarkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
          </button>
          <button className="settings-button" onClick={toggleModal}>
            View Chat History
          </button>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="main-content">
        {/* Chat Container */}
        <div className="chat-container">
          <div className="chat-header">
            <div className="header-content">
              <MessageCircle className="text-blue-500 w-8 h-8 mr-3 animate-pulse" />
              <h1 className="app-title">Medical Assistant</h1>
            </div>
            <div className="header-pills">
              <span className="status-pill">AI powered</span>
              <span className="feature-pill">24/7 Support</span>
            </div>
          </div>

          <div className="messages-container">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message-wrapper ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
              >
                <div className="message-content">
                  {message.text}
                </div>
                <div className="message-timestamp">
                  {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="bot-message">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}
            {messages.length === 0 && (
              <div className="empty-state">
                <div className="empty-state-icon">
                  <Heart className="w-12 h-12 text-blue-400" />
                </div>
                <h3>Start Your Medical Consultation</h3>
                <p>Choose a suggestion or ask any medical question to get started</p>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input Form */}
          <form onSubmit={handleSearch} className="input-container">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Type your medical question..."
              className="message-input"
            />
            <button
              type="submit"
              className="send-button"
              disabled={isLoading || !query.trim()}
            >
              {isLoading ? (
                <Loader className="w-5 h-5 animate-spin" />
              ) : (
                <Send className="w-5 h-5" />
              )}
            </button>
          </form>

          {/* Modal for Chat History */}
          {isModalOpen && (
            <div className="modal-overlay" onClick={handleOverlayClick}>
              <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <h2>Chat History</h2>
                {chatHistory.length === 0 ? (
                  <p>No chat history available.</p>
                ) : (
                  chatHistory.map((entry, index) => (
                    <div key={index} style={{ marginBottom: '10px' }}>
                      <strong>{entry.role === 'user' ? 'You:' : 'Bot:'}</strong> {entry.content}
                    </div>
                  ))
                )}
                <button onClick={toggleModal} style={{ marginTop: '20px' }}>Close</button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
