import React, { useState, useRef, useEffect } from 'react';
import { MessageCircle, Send, Loader, Moon, Sun, Heart, Lock, Unlock, ChevronRight, ChevronLeft, Ribbon, Globe, Database, FileText } from 'lucide-react';

import './App.css';
import MarkdownRenderer from './components/MarkdownRenderer';

// Simplified Prompt Suggestions
const PROMPT_SUGGESTIONS = [
  {
    id: 1,
    lockedIcon: <Lock className="w-6 h-6 text-blue-500" />,
    unlockedIcon: <Unlock className="w-6 h-6 text-blue-500" />,
    title: "Level 1: Foundational Leadership",
  },
  {
    id: 2,
    lockedIcon: <Lock className="w-6 h-6 text-green-500" />,
    unlockedIcon: <Unlock className="w-6 h-6 text-green-500" />,
    title: "Level 2: Strategic Leadership",
  },
  {
    id: 3,
    lockedIcon: <Lock className="w-6 h-6 text-purple-500" />,
    unlockedIcon: <Unlock className="w-6 h-6 text-purple-500" />,
    title: "Level 3: Adaptive Leadership",
  },
  {
    id: 4,
    lockedIcon: <Lock className="w-6 h-6 text-red-500" />,
    unlockedIcon: <Unlock className="w-6 h-6 text-red-500" />,
    title: "Level 4: Visionary Leadership",
  }
];

export default function App() {
  const [messages, setMessages] = useState([]);
  const [query, setQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  const [chatHistory, setChatHistory] = useState([]);
  
  const [unlockedLevels, setUnlockedLevels] = useState({
    1: false,
    2: false,
    3: false,
    4: false
  });
  
  const [currentLevel, setCurrentLevel] = useState(null);

  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    document.body.classList.toggle('dark-mode', isDarkMode);
  }, [messages, isDarkMode]);

  const setLevelContext = async (levelId) => {
    try {
      const response = await fetch('http://localhost:8000/set-level', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ level: levelId }),
      });

      const data = await response.json();
      
      // Add a system message about the level context
      setMessages(prev => [
        ...prev, 
        { 
          text: `ðŸ”“ Level ${levelId} Context Activated: ${data.context_preview}`, 
          sender: 'system' 
        }
      ]);

      return true;
    } catch (error) {
      console.error('Error setting level context:', error);
      return false;
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    // Ensure a level is selected before sending query
    if (currentLevel === null) {
      setMessages(prev => [
        ...prev, 
        { text: "Please select a level first!", sender: 'system' }
      ]);
      return;
    }
  
    const newMessage = { text: query, sender: 'user' };
    setMessages((prev) => [...prev, newMessage]);
    setQuery('');
    setIsLoading(true);
  
    try {
      const response = await fetch('http://localhost:8000/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          question: query,
          level: currentLevel
        }),
      });

      const data = await response.json();
  
      setMessages((prev) => [
        ...prev,
        { text: data.response, sender: 'bot' }
      ]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { text: 'Something went wrong. Please try again.', sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
    }
  };
  
  const handleLevelUnlock = async (levelId) => {
    // Unlock levels sequentially
    if (levelId === 1 || unlockedLevels[levelId - 1]) {
      // Set level context
      const contextSet = await setLevelContext(levelId);
      
      if (contextSet) {
        // Update unlock state
        setUnlockedLevels(prev => ({
          ...prev,
          [levelId]: true
        }));
        
        // Set current active level
        setCurrentLevel(levelId);
      }
    }
  };

  return (
    <div className={`app-container ${isDarkMode ? 'dark-theme' : ''}`}>
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <div className="new-chat-button">
            <Heart className="w-4 h-4 mr-2" />
            CEO Challenge Levels
          </div>
        </div>

        <button 
          className="sidebar-toggle"
          onClick={() => setSidebarOpen(!isSidebarOpen)}
        >
          {isSidebarOpen ? <ChevronLeft /> : <ChevronRight />}
        </button>

        <div className="prompts-list">
          {PROMPT_SUGGESTIONS.map((suggestion) => (
            <div 
              key={suggestion.id} 
              className="prompt-suggestion-item-container"
            >
              <button
                className={`prompt-suggestion-item ${unlockedLevels[suggestion.id] ? 'unlocked' : 'locked'}`}
                onClick={() => handleLevelUnlock(suggestion.id)}
              >
                <div 
                  className="prompt-icon"
                >
                  {unlockedLevels[suggestion.id] 
                    ? suggestion.unlockedIcon 
                    : suggestion.lockedIcon}
                </div>
                <div className="prompt-content">
                  <div className="prompt-title">
                    {suggestion.title}
                  </div>
                  <div className="prompt-preview">
                    {unlockedLevels[suggestion.id] 
                      ? "Active" 
                      : "Locked - Click to Unlock"}
                  </div>
                </div>
              </button>
            </div>
          ))}
        </div>
      </div>

      <div className={`main-content ${!isSidebarOpen ? 'expanded' : ''}`}>
        <div className="chat-container">
          <div className="chat-header">
            <div className="header-content">
              <MessageCircle className="text-blue-500 w-8 h-8 mr-3 animate-pulse" />
              <h1 className="app-title">CEO Challenge Chatbot</h1>
            </div>
          </div>

          <div className="messages-container">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message-wrapper ${
                  message.sender === 'user' ? 'user-message' : 
                  message.sender === 'system' ? 'system-message' : 
                  'bot-message'
                }`}
              >
                <div className="message-content">
                  <MarkdownRenderer content={message.text} />
                </div>
              </div>
            ))}

            {isLoading && (
              <div className="bot-message">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>

          <form onSubmit={handleSearch} className="input-container">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={
                currentLevel 
                  ? `Ask your query for Level ${currentLevel}` 
                  : "Select a level first"
              }
              className="message-input"
              disabled={currentLevel === null}
            />
            <button
              type="submit"
              className="send-button"
              disabled={isLoading || !query.trim() || currentLevel === null}
            >
              {isLoading ? (
                <Loader className="w-5 h-5 animate-spin" />
              ) : (
                <Send className="w-5 h-5" />
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
